function showfunct(){

if((tipoCal==1||tipoCal==5||tipoCal==13)&&(modo_func==0)){ //emergencia?
						document.getElementById('cons').style.display='inline';
						}
if((tipoCal==21||tipoCal==29||tipoCal==53||tipoCal==61||tipoCal==17||tipoCal==49||tipoCal==3||tipoCal==7||tipoCal==15)&&(modo_func==1)){ 
						document.getElementById('consignaDiv').style.display='none';
						document.getElementById('consignaDiv-').style.display='inline';
						}
if(tipoCal==5||tipoCal==7||tipoCal==15||tipoCal==13||tipoCal==21||tipoCal==29||tipoCal==61||tipoCal==53){
	var cadena = "idOperacion=1153";
	$.ajax({
		type: "POST",
		url: "../../recepcion_datos_4.cgi",
		data: cadena,
		async: false,
	}).done(function( data ) {
		var datos=data.split("\n");
		var datos_correctos = eliminarErrores(datos);
		season=datos_correctos[0].replace("CONTROL_CLIMA_INVIERNO=" , "");
		   if(season==1){document.getElementById('season_icon').style.backgroundImage = "url(../images/summer_season.png)";
				if((tipoCal==5||tipoCal==13)&&modo_func==1){ 
						document.getElementById('consignaDiv').style.display='inline';
						document.getElementById('consignaDiv-').style.display='none';
						}
				}
		   else if(season==0){document.getElementById('season_icon').style.backgroundImage = "url(../images/winter_season.png)";}
		   else if(season==2){document.getElementById('season_icon').style.backgroundImage = "url(../images/solo_cal.png)";}
	})
	.fail(function() {
		setTimeout("showfunct()",1101);
	});
}
}

